import { Component, OnInit } from '@angular/core';
import { EmployeeDto, EmployeeViewDto, EmpService, Skill } from 'src/app/services/emp.service';

@Component({
  selector: 'app-emp',
  templateUrl: './emp.component.html',
  styleUrls: ['./emp.component.css']
})
export class EmpComponent implements OnInit {
  skills: Skill[] = [];
  employees: EmployeeViewDto[] = [];

  employee: EmployeeDto = {
    employeeID: '',
    name: '',
    email: '',
    skillIDs: ['']
  };

  isEditMode = false;
  originalEmployeeID = '';

  constructor(private empService: EmpService) {}

  ngOnInit(): void {
    this.loadSkills();
    this.loadEmployees();
  }

  loadSkills(): void {
    this.empService.getSkills().subscribe(data => {
      this.skills = data;
    });
  }

  loadEmployees(): void {
    this.empService.getEmployees().subscribe(data => {
      this.employees = data;
    });
  }

  addSkillField(): void {
    this.employee.skillIDs.push('');
  }

  removeSkillField(index: number): void {
    if (this.employee.skillIDs.length > 1) {
      this.employee.skillIDs.splice(index, 1);
    }
  }

  onSubmit(): void {
    const cleanedSkills = this.employee.skillIDs.filter(id => id && id.trim() !== '');

    const payload: EmployeeDto = {
      ...this.employee,
      skillIDs: cleanedSkills
    };

    if (this.isEditMode) {
      this.empService.updateEmployee(this.originalEmployeeID, payload).subscribe({
        next: () => {
          alert('Employee updated successfully!');
          this.loadEmployees();
          this.resetForm();
        },
        error: () => alert('Error updating employee')
      });
    } else {
      this.empService.addEmployee(payload).subscribe({
        next: () => {
          alert('Employee added successfully!');
          this.loadEmployees();
          this.resetForm();
        },
        error: () => alert('Error adding employee')
      });
    }
  }

  editEmployee(emp: EmployeeViewDto): void {
    this.employee = {
      employeeID: emp.employeeID,
      name: emp.name,
      email: emp.email,
      skillIDs: [] // skill names → IDs to fetch separately
    };

    // fetch original data with skill IDs
    this.empService.getEmployee(emp.employeeID).subscribe(data => {
      this.employee.skillIDs = data.skills.map(skillName => {
        const skill = this.skills.find(s => s.skillName === skillName);
        return skill ? skill.skillID : '';
      });
    });

    this.originalEmployeeID = emp.employeeID;
    this.isEditMode = true;
  }

  deleteEmployee(id: string): void {
    if (confirm('Are you sure to delete this employee?')) {
      this.empService.deleteEmployee(id).subscribe({
        next: () => {
          alert('Employee deleted');
          this.loadEmployees();
        },
        error: () => alert('Error deleting employee')
      });
    }
  }

  resetForm(): void {
    this.employee = {
      employeeID: '',
      name: '',
      email: '',
      skillIDs: ['']
    };
    this.isEditMode = false;
    this.originalEmployeeID = '';
  }
}
<div class="container mt-4">
  <h2 class="mb-4">{{ isEditMode ? 'Edit' : 'Add' }} Employee</h2>

  <form (ngSubmit)="onSubmit()">
    <div class="mb-3">
      <label class="form-label">Employee ID</label>
      <input type="text" class="form-control" [(ngModel)]="employee.employeeID" name="employeeID" [readonly]="isEditMode" required />
    </div>

    <div class="mb-3">
      <label class="form-label">Name</label>
      <input type="text" class="form-control" [(ngModel)]="employee.name" name="name" required />
    </div>

    <div class="mb-3">
      <label class="form-label">Email</label>
      <input type="email" class="form-control" [(ngModel)]="employee.email" name="email" required />
    </div>

    <div class="mb-3">
      <label class="form-label">Skills</label>
      <div *ngFor="let skillId of employee.skillIDs; let i = index" class="d-flex align-items-center mb-2">
        <select class="form-select me-2" [(ngModel)]="employee.skillIDs[i]" [name]="'skill' + i" required>
          <option value="">-- Select Skill --</option>
          <option *ngFor="let skill of skills" [value]="skill.skillID">{{ skill.skillName }}</option>
        </select>
        <button type="button" class="btn btn-danger btn-sm me-1" (click)="removeSkillField(i)" *ngIf="employee.skillIDs.length > 1">Remove</button>
        <button type="button" class="btn btn-success btn-sm" (click)="addSkillField()" *ngIf="i === employee.skillIDs.length - 1">Add</button>
      </div>
    </div>

    <button type="submit" class="btn btn-primary">{{ isEditMode ? 'Update' : 'Save' }}</button>
    <button type="button" class="btn btn-secondary ms-2" (click)="resetForm()" *ngIf="isEditMode">Cancel</button>
  </form>

  <hr class="my-4" />

  <h4>Employee List</h4>
  <table class="table table-bordered mt-3">
    <thead>
      <tr>
        <th>ID</th><th>Name</th><th>Email</th><th>Skills</th><th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let emp of employees">
        <td>{{ emp.employeeID }}</td>
        <td>{{ emp.name }}</td>
        <td>{{ emp.email }}</td>
        <td>{{ emp.skills.join(', ') }}</td>
        <td>
          <button class="btn btn-sm btn-warning me-1" (click)="editEmployee(emp)">Edit</button>
          <button class="btn btn-sm btn-danger" (click)="deleteEmployee(emp.employeeID)">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
