import { Component, OnInit } from '@angular/core';
import { EmployeeDto, EmployeeViewDto, EmpService, Skill } from 'src/app/services/emp.service';

@Component({
  selector: 'app-emp',
  templateUrl: './emp.component.html',
  styleUrls: ['./emp.component.css']
})
export class EmpComponent implements OnInit {
  skills: Skill[] = [];
  allEmployees: EmployeeViewDto[] = [];

  employee: EmployeeDto = {
    employeeID: '',
    name: '',
    email: '',
    skillIDs: [''] // start with one skill dropdown
  };

  insertedEmployee: EmployeeViewDto | null = null;

  constructor(private employeeService: EmpService) {}

  ngOnInit(): void {
    this.loadSkills();
    this.loadAllEmployees();
  }

  loadSkills(): void {
    this.employeeService.getSkills().subscribe({
      next: data => this.skills = data,
      error: err => console.error('Failed to load skills', err)
    });
  }

  loadAllEmployees(): void {
    this.employeeService.getAllEmployees().subscribe({
      next: data => this.allEmployees = data,
      error: err => console.error('Failed to load employees', err)
    });
  }

  addSkillField(): void {
    this.employee.skillIDs.push('');
  }

  removeSkillField(index: number): void {
    if (this.employee.skillIDs.length > 1) {
      this.employee.skillIDs.splice(index, 1);
    }
  }

  onSubmit(): void {
    const cleanedSkillIDs = this.employee.skillIDs.filter(id => id && id.trim().length > 0);

    const payload: EmployeeDto = {
      ...this.employee,
      skillIDs: cleanedSkillIDs
    };

    this.employeeService.addEmployee(payload).subscribe({
      next: (res) => {
        this.insertedEmployee = res;
        alert('Employee saved successfully!');

        // Add new employee to the table list
        this.allEmployees.push(res);

        // Reset form
        this.employee = { employeeID: '', name: '', email: '', skillIDs: [''] };
      },
      error: (err) => {
        console.error('Error saving employee', err);
        alert('Failed to save employee');
      }
    });
  }
}
<div class="container mt-4">
  <h2 class="mb-4">Add Employee</h2>

  <form (ngSubmit)="onSubmit()">
    <div class="mb-3">
      <label for="employeeID" class="form-label">Employee ID</label>
      <input type="text" class="form-control" id="employeeID" [(ngModel)]="employee.employeeID" name="employeeID" required />
    </div>

    <div class="mb-3">
      <label for="name" class="form-label">Name</label>
      <input type="text" class="form-control" id="name" [(ngModel)]="employee.name" name="name" required />
    </div>

    <div class="mb-3">
      <label for="email" class="form-label">Email</label>
      <input type="email" class="form-control" id="email" [(ngModel)]="employee.email" name="email" required />
    </div>

    <div class="mb-3">
      <label class="form-label">Skills</label>
      <div *ngFor="let skillId of employee.skillIDs; let i = index" class="d-flex align-items-center mb-2">
        <select class="form-select me-2" [(ngModel)]="employee.skillIDs[i]" [name]="'skill' + i" required>
          <option value="">-- Select Skill --</option>
          <option *ngFor="let skill of skills" [value]="skill.skillID">{{ skill.skillName }}</option>
        </select>
        <button type="button" class="btn btn-danger btn-sm me-1" (click)="removeSkillField(i)" *ngIf="employee.skillIDs.length > 1" title="Remove this skill">Remove</button>
        <button type="button" class="btn btn-success btn-sm" (click)="addSkillField()" *ngIf="i === employee.skillIDs.length - 1" title="Add new skill">Add</button>
      </div>
    </div>

    <button type="submit" class="btn btn-primary">Save</button>
  </form>

  <div *ngIf="insertedEmployee" class="mt-4">
    <h4>Inserted Employee Info</h4>
    <p><strong>ID:</strong> {{ insertedEmployee.employeeID }}</p>
    <p><strong>Name:</strong> {{ insertedEmployee.name }}</p>
    <p><strong>Email:</strong> {{ insertedEmployee.email }}</p>
    <p><strong>Skills:</strong> {{ insertedEmployee.skills.join(', ') }}</p>
  </div>
</div>
