import { Component, OnInit } from '@angular/core';
import { EmployeeDto, EmployeeViewDto, EmpService, Skill } from 'src/app/services/emp.service';

@Component({
  selector: 'app-emp',
  templateUrl: './emp.component.html',
  styleUrls: ['./emp.component.css']
})
export class EmpComponent implements OnInit {
  skills: Skill[] = [];
  employees: EmployeeViewDto[] = [];

  employee: EmployeeDto = {
    employeeID: '',
    name: '',
    email: '',
    skillIDs: ['']
  };

  isEditMode = false;
  originalEmployeeID = '';

  constructor(private empService: EmpService) {}

  ngOnInit(): void {
    this.loadSkills();
    this.loadEmployees();
  }

  loadSkills(): void {
    this.empService.getSkills().subscribe(data => {
      this.skills = data;
    });
  }

  loadEmployees(): void {
    this.empService.getEmployees().subscribe(data => {
      this.employees = data;
    });
  }

  addSkillField(): void {
    this.employee.skillIDs.push('');
  }

  removeSkillField(index: number): void {
    if (this.employee.skillIDs.length > 1) {
      this.employee.skillIDs.splice(index, 1);
    }
  }

  onSubmit(): void {
    const cleanedSkills = this.employee.skillIDs.filter(id => id && id.trim() !== '');

    const payload: EmployeeDto = {
      ...this.employee,
      skillIDs: cleanedSkills
    };

    if (this.isEditMode) {
      this.empService.updateEmployee(this.originalEmployeeID, payload).subscribe({
        next: () => {
          alert('Employee updated successfully!');
          this.loadEmployees();
          this.resetForm();
        },
        error: () => alert('Error updating employee')
      });
    } else {
      this.empService.addEmployee(payload).subscribe({
        next: () => {
          alert('Employee added successfully!');
          this.loadEmployees();
          this.resetForm();
        },
        error: () => alert('Error adding employee')
      });
    }
  }

  editEmployee(emp: EmployeeViewDto): void {
    this.employee = {
      employeeID: emp.employeeID,
      name: emp.name,
      email: emp.email,
      skillIDs: [] // skill names → IDs to fetch separately
    };

    // fetch original data with skill IDs
    this.empService.getEmployee(emp.employeeID).subscribe(data => {
      this.employee.skillIDs = data.skills.map(skillName => {
        const skill = this.skills.find(s => s.skillName === skillName);
        return skill ? skill.skillID : '';
      });
    });

    this.originalEmployeeID = emp.employeeID;
    this.isEditMode = true;
  }

  deleteEmployee(id: string): void {
    if (confirm('Are you sure to delete this employee?')) {
      this.empService.deleteEmployee(id).subscribe({
        next: () => {
          alert('Employee deleted');
          this.loadEmployees();
        },
        error: () => alert('Error deleting employee')
      });
    }
  }

  resetForm(): void {
    this.employee = {
      employeeID: '',
      name: '',
      email: '',
      skillIDs: ['']
    };
    this.isEditMode = false;
    this.originalEmployeeID = '';
  }
}
<div class="container mt-5">
  <h2 class="mb-4 text-primary fw-bold">
    {{ isEditMode ? 'Edit Employee' : 'Add New Employee' }}
  </h2>

  <form (ngSubmit)="onSubmit()" class="border rounded p-4 shadow-sm bg-light">
    <div class="mb-3">
      <label for="employeeID" class="form-label fw-semibold">Employee ID</label>
      <input
        type="text"
        id="employeeID"
        class="form-control"
        [(ngModel)]="employee.employeeID"
        name="employeeID"
        [readonly]="isEditMode"
        required
      />
    </div>

    <div class="mb-3">
      <label for="name" class="form-label fw-semibold">Name</label>
      <input
        type="text"
        id="name"
        class="form-control"
        [(ngModel)]="employee.name"
        name="name"
        required
      />
    </div>

    <div class="mb-3">
      <label for="email" class="form-label fw-semibold">Email</label>
      <input
        type="email"
        id="email"
        class="form-control"
        [(ngModel)]="employee.email"
        name="email"
        required
      />
    </div>

    <div class="mb-3">
      <label class="form-label fw-semibold">Skills</label>
      <div *ngFor="let skillId of employee.skillIDs; let i = index" class="d-flex align-items-center mb-2">
        <select
          class="form-select me-2"
          [(ngModel)]="employee.skillIDs[i]"
          [name]="'skill' + i"
          required
        >
          <option value="">-- Select Skill --</option>
          <option *ngFor="let skill of skills" [value]="skill.skillID">
            {{ skill.skillName }}
          </option>
        </select>

        <button
          type="button"
          class="btn btn-outline-danger btn-sm me-1"
          (click)="removeSkillField(i)"
          *ngIf="employee.skillIDs.length > 1"
        >
          Remove
        </button>

        <button
          type="button"
          class="btn btn-outline-success btn-sm"
          (click)="addSkillField()"
          *ngIf="i === employee.skillIDs.length - 1"
        >
          Add
        </button>
      </div>
    </div>

    <div class="d-flex gap-2 mt-4">
      <button type="submit" class="btn btn-primary">
        {{ isEditMode ? 'Update' : 'Save' }}
      </button>
      <button
        type="button"
        class="btn btn-secondary"
        (click)="resetForm()"
        *ngIf="isEditMode"
      >
        Cancel
      </button>
    </div>
  </form>

  <hr class="my-5" />

  <h4 class="mb-3 text-secondary fw-semibold">Employee List</h4>
  <div class="table-responsive">
    <table class="table table-bordered table-striped align-middle">
      <thead class="table-primary">
        <tr>
          <th scope="col">ID</th>
          <th scope="col">Name</th>
          <th scope="col">Email</th>
          <th scope="col">Skills</th>
          <th scope="col" class="text-center">Actions</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let emp of employees">
          <td>{{ emp.employeeID }}</td>
          <td>{{ emp.name }}</td>
          <td>{{ emp.email }}</td>
          <td>{{ emp.skills.join(', ') }}</td>
          <td class="text-center">
            <button class="btn btn-sm btn-warning me-2" (click)="editEmployee(emp)">
              Edit
            </button>
            <button class="btn btn-sm btn-danger" (click)="deleteEmployee(emp.employeeID)">
              Delete
            </button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
